targets.expr:
sum by (pool_id, name) (
  ceph_pool_max_avail * on(pool_id) group_left(name)
  ceph_pool_metadata{name="storage_data"}
)
+
sum by (pool_id, name) (
  ceph_pool_stored * on(pool_id) group_left(name)
  ceph_pool_metadata{name="storage_data"}
)

sum by (pool_id, name) (
  ceph_pool_stored * on(pool_id) group_left(name)
  ceph_pool_metadata{name=~"storage_.*"}
)

sum by (pool_id, name) (
  (ceph_pool_compress_under_bytes - ceph_pool_compress_bytes_used) * on(pool_id) group_left(name)
  ceph_pool_metadata{name=~"storage_data"}
)

sum by (pool_id, name) (
  ceph_pool_stored * on(pool_id) group_left(name)
  ceph_pool_metadata{name=~"storage_.*"}
)

sum by (pool_id, name) (
  ceph_pool_max_avail * on(pool_id) group_left(name)
  ceph_pool_metadata{name=~"storage_data"}
)
+
sum by (pool_id, name) (
  ceph_pool_stored * on(pool_id) group_left(name)
  ceph_pool_metadata{name=~"storage_data"}
)

sum by (pool_id, name) (
  (ceph_pool_compress_under_bytes - ceph_pool_compress_bytes_used) * on(pool_id) group_left(name)
  ceph_pool_metadata{name=~"storage_data"}
)
